mapper.select:
SELECT vc_order_info.goods_no AS goodsNo, vc_order_info.goods_name AS goodsName, SUM(vc_order_info.amount) AS amount, SUM(vc_order_info.goods_num) AS goodsNum FROM vc_order_info WHERE vc_order_info.is_delete = 0 AND vc_order_info.status != 0 AND vc_order_info.status != 2 AND vc_order_info.status != 5 AND vc_order_info.status != 6
and vc_order_info.insert_time >= #{startTime}
and vc_order_info.insert_time <= #{endTime}
GROUP BY vc_order_info.goods_no
ORDER BY goodsNum DESC
ORDER BY amount DESC

SELECT SUM(vc_order_info.amount) AS amount FROM vc_order_info WHERE vc_order_info.is_delete = 0 AND vc_order_info.status != 0 AND vc_order_info.status != 2 AND vc_order_info.status != 5 AND vc_order_info.status != 6
and vc_order_info.insert_time >= #{startTime}
and vc_order_info.insert_time <= #{endTime}
