mapper.select:
SELECT wine_user_relation.id, wine_user_relation.parent_user_id, wine_user_relation.child_user_id, wine_user_relation.created_at FROM wine_user_relation LEFT JOIN vc_user_user ON wine_user_relation.parent_user_id = vc_user_user.id WHERE wine_user_relation.is_delete = 0
AND vc_user_user.`phone` LIKE CONCAT("%",#{phone},"%")
AND vc_user_user.`nickname` LIKE CONCAT("%",#{nickname},"%")
AND wine_user_relation.parent_user_id = #{parentUserId}
AND wine_user_relation.child_user_id = #{childUserId}
