

CREATE TABLE `z9_payment_config`
(
    `Z9_ID`              char(32) NOT NULL,
    `Z9_TENANT_ID`       char(32)    DEFAULT '0' COMMENT '租户id',
    `Z9_PAYMENT_CHANNEL` int(11)     DEFAULT NULL COMMENT '支付渠道',
    `Z9_PROXY_PAYMENT`   int(11)     DEFAULT NULL COMMENT '是否代理支付',
    `Z9_PARENT_ID`       char(32)    DEFAULT NULL COMMENT '当使用了代理支付模式时, 需要关联父级配置',
    `Z9_CONTENT`         text COMMENT '配置内容',
    `Z9_CREATOR`         varchar(32) DEFAULT NULL COMMENT '创建人',
    `Z9_UPDATER`         varchar(32) DEFAULT NULL COMMENT '更信任',
    `Z9_INSERT_TIME`     datetime    DEFAULT NULL COMMENT '创建时间',
    `Z9_UPDATE_TIME`     datetime    DEFAULT NULL COMMENT '更新时间',
    PRIMARY KEY (`Z9_ID`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='支付配置表';


